exports.syncBangCongSangDiemDanh = async (req, res, DATA) => {
  let checkkq = "OK";
  let setpdQuery = `
  MERGE INTO ZTBATTENDANCETB AS target
USING (SELECT ZTB_BANG_CONG.*,ZTBEMPLINFO.WORK_SHIFT_CODE,ZTBEMPLINFO.CALV FROM ZTB_BANG_CONG LEFT JOIN ZTBEMPLINFO ON ZTB_BANG_CONG.CTR_CD = ZTBEMPLINFO.CTR_CD AND ZTB_BANG_CONG.EMPL_NO = ZTBEMPLINFO.EMPL_NO WHERE ZTB_BANG_CONG.CTR_CD = '${DATA.CTR_CD}' AND ZTB_BANG_CONG.APPLY_DATE BETWEEN '${DATA.FROM_DATE}' AND '${DATA.TO_DATE}') AS source
ON target.CTR_CD = source.CTR_CD 
   AND target.EMPL_NO = source.EMPL_NO 
   AND target.APPLY_DATE = source.APPLY_DATE
WHEN MATCHED THEN 
    UPDATE SET 
        target.WORK_HOUR = source.WORK_HOUR,
        target.IN_TIME = source.IN_TIME,
        target.OUT_TIME = source.OUT_TIME
WHEN NOT MATCHED THEN 
    INSERT (CTR_CD, EMPL_NO, APPLY_DATE,ON_OFF, OVERTIME,CURRENT_TEAM, CURRENT_CA,  WORK_HOUR, IN_TIME, OUT_TIME)
    VALUES (source.CTR_CD, source.EMPL_NO, source.APPLY_DATE,1, CASE WHEN source.WORK_HOUR > 8 THEN 1 ELSE 0 END, source.WORK_SHIFT_CODE,isnull(source.CALV,0), source.WORK_HOUR, source.IN_TIME, source.OUT_TIME);
`;
  //${moment().format('YYYY-MM-DD')}
  console.log(setpdQuery);
  checkkq = await queryDB(setpdQuery);
  res.send(checkkq);
};
